Tour.create!(title:'Đà Lạt – Que Garden – Đồi Chè Cầu Đất – Nông Trại Cún Puppy Farm',start_date:Date.parse('01-06-2021'), start_hour: Time.now.strftime("%I:%M %p"),end_date:Date.parse('03-06-2021'), days: "3", price: "1990000", slot: "15", remain_slot: "8",departure: "Hồ Chí Minh", destination: "Đà Lạt", tour_type: TourType.first, notice: "Ữu đãi khi thanh toán trực tuyến", photo: "")
TourProgram.create!(tour: Tour.find_by(title:'Đà Lạt – Que Garden – Đồi Chè Cầu Đất – Nông Trại Cún Puppy Farm'), description:'Đà Lạt tinh khôi, trong lành giữa rừng thông cùng muôn hoa đua nở dưới nắng dịu dàng và những cảnh đẹp nao lòng luôn hấp dẫn du khách thập phương. Đà Lạt luôn không ngừng đổi mới và khiến người ta say đắm quên lối về với một sắc thái, một nét đẹp riêng vốn có', tour_program_date: Date.parse('01-06-2021'), title:'TP. HỒ CHÍ MINH - ĐÀ LẠT', description_by_date:'Quý khách tập trung tại điểm hẹn, xe đưa đoàn khởi hành đi Đà Lạt. Đoàn dừng cơm trưa tại Bảo Lộc, tiếp tục hành trình đến với thành phố ngàn hoa. Đến Đà Lạt, Quý khách sẽ dừng chân tham quan:  Quê Garden: nằm dưới chân đèo Mimosa, khu vườn mê mẩn người xem với cánh đồng hoa rộng lớn cùng nhiều loài hoa nổi tiếng tại Đà Lạt khoe sắc. Đặc biệt Quê Garden tự hào là khu vườn bonsai lá kim lớn nhất Việt Nam, những chậu cây bonsai vừa to vừa đẹp, được chăm sóc và uốn nắn kỹ lưỡng vô cùng đẹp mắt. Ngoài ra, hồ cá koi với view nhìn ra đồi thông là điểm check in cực chất mà du khách không thể bỏ qua. Về khách sạn, nhận phòng nghỉ ngơi và ăn tối. Buổi tối, Quý khách tự do dạo chợ đêm Đà Lạt, sắm cho mình chiếc khăn hoặc áo len để ghi lại những bức ảnh rực rỡ tại phố núi xinh đẹp. Nghỉ đêm tại Đà Lạt', position: '1')

 TourProgram.create!(tour: Tour.find_by(title:'Đà Lạt – Que Garden – Đồi Chè Cầu Đất – Nông Trại Cún Puppy Farm'), description:' ', tour_program_date: Date.parse('02-06-2021'), title:'ĐÀ LẠT - LÀNG HOA VẠN THÀNH - PHÂN VIỆN SINH HỌC - GALLERY CHOCOLATE - PUPPY FARM', description_by_date:'Chùa Linh Phước: hay còn được gọi là chùa Rồng, chùa Ve Chai, ngôi chùa nổi tiếng bậc nhất xứ sở ngàn hoa và đang giữ riêng cho mình tới 11 kỷ lục gia. Làng Hoa Vạn Thành: làng hoa nổi tiếng và lớn nhất tại Đà Lạt. Tại đây, du khách sẽ bị choáng ngợp bởi vẻ yêu kiều, quyến rũ của bạt ngàn những đóa Hồng, Cẩm Chướng, Lily, Đồng Tiền, Cẩm Tú Cầu… Ngoài ra, Quý khách còn được khám phá và thỏa sức chụp hình tại vườn Bí Ngô khổng lồ, Ớt Đà Lạt, Dưa Pepino, Dâu và các loại rau trồng thủy canh (tùy theo mùa). Ăn trưa tại nhà hàng. Buổi chiều, đoàn tiếp tục tham quan những điểm đến mới lạ và độc đáo như: Phân Viện Sinh Học Đà Lạt: được biết đến như 1 tọa độ sống ảo mới tại Đà Lạt với góc ảnh bên những bức tường cổ kính. Không chỉ vậy, đến đây, du khách còn được tìm hiểu về môi trường sống, những nguồn gen quý qua hiện vật vô cùng phong phú, khu trưng bày xương, sừng, mô hình của các loài động vật quý hiếm như khủng long, voi, hổ, gấu, vượn, … hay tìm hiểu về sự vận hành của vũ trụ, Gallery Chocolate: với nhiều không gian trưng bày được thiết kế bởi màu sắc, tạo hình và hương vị đặc biệt từ nguồn nguyên liệu của Chocotea. Ấn tượng nhất là không gian nghệ thuật với 9 tác phẩm được tạo tác công phu từ chocolate như: “Bàn tiệc hoàng gia”, Cây Mai Anh Đào hay tác phẩm “Suối Hoa” tuyệt mỹ, … Du khách dùng thử chocolate để cảm nhận hương vị khác biệt từ hương đến sắc', position: '2')
TourProgram.create!(tour: Tour.find_by(title:'Đà Lạt – Que Garden – Đồi Chè Cầu Đất – Nông Trại Cún Puppy Farm'), description:' ', tour_program_date: Date.parse('03-06-2021'), title:'ĐÀ LẠT - MÊ LINH COFFEE - TP. HỒ CHÍ MINH', description_by_date:'Sau khi dùng bữa sáng, đoàn trả phòng và tham quan: Cà Phê Mê Linh: được thiết kế với không gian mở có một tầm nhìn trọn vẹn 360 độ hướng rẫy cà phê bạt ngàn... Đặc biệt, thưởng thức một ly cà phê chồn nguyên chất với hương vị đậm đà đặc trưng của chất xạ hương (chi phí tự túc). Ngoài ra, Quý khách có thể check in những phiên bản thu nhỏ nhưng không kém phần sống động của những công trình nổi tiếng như "cổng trời Bali, cổng trời Linh Quy Pháp Ấn, nấc thang lên thiên đường, tổ chim.. Chùa Linh Ẩn: kiến trúc toát lên vẻ hiện đại và thân thiện với thiên nhiên với toàn bộ diện tích đất hơn 4 ha của chùa được phủ xanh với những loại cây rừng như gió, sộp, si, bằng lăng...; Quý khách chiêm bái tượng Phật Quan Thế Âm cao 54 mét (cao nhất Lâm Đồng) và check-in 1 góc thác Voi hùng vĩ. Đoàn khởi hành về lại TP. Hồ Chí Minh, trả khách tại điểm đón ban đầu. Kết thúc hành trình nhiều trải nghiệm', position: '3')
